import React, { Component } from "react";
import Parallax from "react-rellax";
import $ from "jquery";

//COMPONENTS
import Line from "../2_module/Line";
import BackgroundImg from "./BackgroundImg";

//STYLES
import { HeaderStyle } from "../../2_styles/1_layout/HeaderStyle";

export default class Header extends Component {
  componentDidMount() {
    $(window).scroll(function() {
      var scroll = $(window).scrollTop();
      $(".bgImg-header").css({
        "-webkit-filter": "blur(" + scroll / 200 + "px)",
        filter: "blur(" + scroll / 150 + "px)"
      });
    });
  }

  render() {
    const { bgImg, the, title, subtitle, bgPosition, position } = this.props;

    return (
      <div>
        <HeaderStyle position={position}>
          <div className="bgImg-header">
            <Parallax speed={1}>
              <BackgroundImg
                bgImg={bgImg}
                height="120vh"
                bgPosition={bgPosition}
                bgFixed="fixed"
              />
            </Parallax>
          </div>
          <Parallax speed={-7}>
            <div className="header_text">
              <p className="pretitle">{the}</p>
              <p className="title">{title}</p>
              <Line />
              <p className="subtitle">{subtitle}</p>
            </div>
          </Parallax>
        </HeaderStyle>
      </div>
    );
  }
}
