import React, { Component } from "react";
import { Formik } from "formik";
import * as Yup from "yup";

import { FormStyle } from "../../2_styles/3_module/FormStyle";

const validationSchema = Yup.object().shape({
  superName: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Must enter a name"),
  email: Yup.string()
    .email("Must be a valid email")
    .max(255, "Must be shorter than 255")
    .required("Must enter a email")
});

export default function Form() {
  return (
    <div>
      <FormStyle>
        <Formik
          initialValues={{ superName: "", email: "" }}
          validationSchema={validationSchema}
          onSubmit={(values, { setSubmitting, resetForm }) => {
            setSubmitting(true);
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              resetForm();
              setSubmitting(false);
            }, 500);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting
          }) => (
            <form id="myForm">
              <div className="input_row">
                <label htmlFor="superName">What's your superhero name?</label>
                <input
                  type="text"
                  name="superName"
                  id="superName"
                  placeholder="Chapolin Colorado"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.superName}
                  className={
                    touched.superName && errors.superName ? "input_error" : null
                  }
                />
              </div>

              <div className="input_row">
                <label htmlFor="realName">What's your real name?</label>
                <input
                  type="text"
                  name="realName"
                  id="realName"
                  placeholder="Peter Parker"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.realName}
                  classname={
                    touched.realName && errors.realName ? "input_error" : null
                  }
                />
              </div>

              <div className="input_row">
                <label htmlFor="abilities">What are your superpowers?</label>
                <input
                  type="text"
                  name="abilities"
                  id="abilities"
                  placeholder="Fly, super-strenght, lick your elbow. That kind of thing."
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.abilities}
                  classname={
                    touched.abilities && errors.abilities ? "input_error" : null
                  }
                />
              </div>

              <div className="input_row">
                <label htmlFor="country">What country do you live in?</label>
                <input
                  type="text"
                  name="country"
                  id="country"
                  placeholder="Wakanda"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.country}
                  classname={
                    touched.country && errors.country ? "input_error" : null
                  }
                />
              </div>

              <div className="input_row">
                <label htmlFor="retiredHuman">
                  Have you ever retired a human by mistake?
                </label>
                <input
                  type="radio"
                  name="retiredHuman"
                  id="retiredHuman"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.retiredHuman}
                  classname={
                    touched.retiredHuman && errors.retiredHuman
                      ? "input_error"
                      : null
                  }
                />
                Yes
                <input
                  type="radio"
                  name="retiredHuman"
                  id="retiredHuman"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.retiredHuman}
                  classname={
                    touched.retiredHuman && errors.retiredHuman
                      ? "input_error"
                      : null
                  }
                />
                No
              </div>

              <div className="input_row">
                <button type="submit" disabled={isSubmitting}>
                  Submit
                </button>
              </div>
            </form>
          )}

