import styled from "styled-components";

/* .bgImg_shadow {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImgShadow};

    background: linear-gradient(to top, #111111, transparent 60%);
  }

  .bgImg {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImg};

    background-image: url(${props => props.bgImg});
    background-position: center;
    background-size: cover;
  } */
export const BackgroundImgStyle = styled.div`
  .bg_bog {
    margin: 0;
    padding: 0;
    perspective: 1px;
    transform-style: preserve-3d;
    /* height: 100%; */
    overflow-y: scroll;
    overflow-x: hidden;
    font-family: "Luna";
  }

  .bgImg {
    box-sizing: border-box;
    min-height: 100vh;
    padding: 30vw 0 5vw;
    position: relative;
    transform-style: inherit;
    width: 100vw;
  }

  .bgImg,
  .bgImg:before {
    background: 50% 50% / cover;
  }
  .bgImg::before {
    content: "";

    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;

    display: block;
    background-image: url(${props => props.bgImg});
    background-size: cover;
    transform-origin: center center 0;
    background-repeat: no-repeat;
    transform: translateZ(-1px) scale(2);
    z-index: -1;
    min-height: 100vh;
  }
`;
