import React, { Component } from "react";

import { Formik } from "formik";
import * as Yup from "yup";
import Parallax from "react-rellax";

import submitIcon from "../../3_images/4_becomeAnAvenger/icon_submit.svg";
import { FormStyle } from "../../2_styles/3_module/FormStyle";

import FormError from "../2_module/FormError";

const validationSchema = Yup.object().shape({
  superName: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Please enter your superhero name"),
  realName: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255")
    .required("Please enter your real name"),
  superpower: Yup.string()
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Please enter at least one superpower"),
  country: Yup.string() // CREATE COUNTRY LIST
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Please choose your country"),
  retiredHuman: Yup.string() // BOOLEAN??
    .min(2, "Must have at least 2 characters")
    .max(255, "Must be shorter than 255 characters")
    .required("Please select an answer"),
  email: Yup.string()
    .email("Must be a valid email")
    .max(255, "Must be shorter than 255 characters")
    .required("Please enter your email")
});

export default class Form extends Component {
  nextQuestion = () => {
    alert("hi");
  };

  render() {
    return (
      <div>
        <FormStyle>
          <Formik
            initialValues={{
              superName: "",
              realName: "",
              superpower: "",
              country: "",
              retiredHuman: "",
              email: ""
            }}
            validationSchema={validationSchema}
            onSubmit={(values, { setSubmitting, resetForm }) => {
              setSubmitting(true);
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                resetForm();
                setSubmitting(false);
              }, 500);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting
            }) => (
              <form id="myForm" onSubmit={handleSubmit}>
                <div className="input_row">
                  <label htmlFor="superName">
                    What is your superhero name?
                  </label>
                  <input
                    type="text"
                    name="superName"
                    id="superName"
                    placeholder="Chapolin Colorado"
                    onChange={handleChange}
                    onBlur={(handleBlur, this.nextQuestion)}
                    value={values.superName}
                    className={
                      touched.superName && errors.superName
                        ? "input_error"
                        : null
                    }
                  />
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <div className="input_row">
                  <label htmlFor="realName">What is your real name?</label>
                  <input
                    type="text"
                    name="realName"
                    id="realName"
                    placeholder="Peter Parker"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.realName}
                    classname={
                      touched.realName && errors.realName ? "input_error" : null
                    }
                  />
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <div className="input_row">
                  <label htmlFor="superpower">What are your superpowers?</label>
                  <input
                    type="text"
                    name="superpower"
                    id="superpower"
                    placeholder="Fly, super-strenght, lick your elbow. That kind of thing."
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.superpower}
                    classname={
                      touched.superpower && errors.superpower
                        ? "input_error"
                        : null
                    }
                  />
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <div className="input_row">
                  <label htmlFor="country">What country do you live in?</label>
                  <input
                    type="text"
                    name="country"
                    id="country"
                    placeholder="Wakanda"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.country}
                    classname={
                      touched.country && errors.country ? "input_error" : null
                    }
                  />
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <div className="input_row">
                  <label htmlFor="retiredHuman">
                    Have you ever retired a human by mistake?
                  </label>
                  <input
                    type="radio"
                    name="retiredHuman"
                    id="retiredHuman"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.retiredHuman}
                    classname={
                      touched.retiredHuman && errors.retiredHuman
                        ? "input_error"
                        : null
                    }
                  />
                  <span>Yes</span>
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                  <input
                    type="radio"
                    name="retiredHuman"
                    id="retiredHuman"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.retiredHuman}
                    classname={
                      touched.retiredHuman && errors.retiredHuman
                        ? "input_error"
                        : null
                    }
                  />
                  <span>No</span>
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <div className="input_row">
                  <label htmlFor="email">What's your email?</label>
                  <input
                    type="email"
                    name="email"
                    id="email"
                    placeholder="email@example.com"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.email}
                    classname={
                      touched.email && errors.email ? "input_error" : null
                    }
                  />
                  <FormError
                    touched={touched.superName}
                    message={errors.superName}
                  />
                </div>

                <div className="btn_submit-box">
                  <button id="btn_submit" type="submit" disabled={isSubmitting}>
                    Submit
                  </button>
                </div>
              </form>
            )}
          </Formik>
        </FormStyle>
      </div>
    );
  }
}
