import React from "react";
import { Parallax, Background } from "react-parallax";

import { SectionStyle } from "../../2_styles/1_layout/SectionStyle";
import BackgroundImg from "./BackgroundImg";

export default function Section({ bgImg, title, description }) {
  return (
    <SectionStyle>
      {/* <BackgroundImg bgImg={bgImg} zIndexImg="-2" zIndexImgShadow="-1" /> */}

      {/* <BackgroundImg bgImg={bgImg} /> */}

      <div className="parallax">
        <Parallax
          bgImage={bgImg}
          bgImageAlt="the cat"
          strength={400}
          blur={{ min: -8, max: 20 }}
          bgClassName={"parallax_bg"}
        >
          <div className="bgImg_shadow" style={{ height: "100vh" }} />
        </Parallax>
      </div>

      <div className="text">
        <div className="title"> {title} </div>
        <div className="description">{description}</div>
      </div>
    </SectionStyle>
  );
}
