import styled from "styled-components";

export const FormStyle = styled.div`
  #myForm {
    z-index: 1;

    height: 100vh;
    width: 100;
    padding: 0 5vw 0 5vw;

    font-size: 2rem;
    font-family: "Cormorant Infant", serif;
  }

  .input_row {
    width: 100%;
    position: absolute;
    margin-top: 16rem;
    margin: 0 -5vw 0 -5vw;

    display: none;

    &:nth-child(1) {
      display: inline;
    }
  }

  input {
    display: block;
    font-size: 1.5rem;
    font-family: "Roboto", sans-serif;
    font-weight: 100;

    ::placeholder {
      color: rgba(240, 240, 240, 0.5);
    }
  }

  .input_error {
    border: 1px solid crimson;
  }

  .line {
    height: 1px;
    width: 100%;

    position: absolute;
    margin-top: 20rem;
    /* margin-left: 5vw; */

    background: linear-gradient(to right, #f0f0f0, transparent);
  }

  button {
    cursor: pointer;
    color: #f0f0f0;
    font-family: "roboto", sans-serif;
  }

  #submit_icon {
    filter: invert(100);
    width: 1rem;
  }
`;
