import styled from "styled-components";

export const BackgroundImgStyle = styled.div`
  /* .bgImg_shadow {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImgShadow};

    background: linear-gradient(to top, #111111, transparent 60%);
  }

  .bgImg {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImg};

    background-image: url(${props => props.bgImg});
    background-position: center;
    background-size: cover;
  } */

  .bg_bog {
    margin: 0;
  padding: 0;
  perspective: 1px;
  transform-style: preserve-3d;
  height: 100%;
  overflow-y: scroll;
  overflow-x: hidden;
  font-family: "Luna";
  }
`;
