import styled from "styled-components";

export const HeaderStyle = styled.div`
  .title {
    height: 250px;

    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

    font-family: "Cormorant Infant", serif;
    font-size: 1.5em;
    text-align: center;

    p:nth-child(2) {
      font-size: 2em;
      text-transform: uppercase;
    }
  }
`;
