import React, { Component } from "react";
import Parallax from "parallax-js";

import { BackgroundImgStyle } from "../../2_styles/1_layout/BackgroundImgStyle";

export default class BackgroundImg extends Component {
  componentDidMount() {
    this.parallax = new Parallax(this.scene);
  }
  componentWillUnmount() {
    this.parallax.disable();
  }

  render() {
    const { left, bgImg, bgFixed } = this.props;
    return (
      <div>
        <div
          class="parallax-window"
          data-parallax="scroll"
          data-image-src={bgImg}
          data-speed=".5"
        ></div>

        {/* <BackgroundImgStyle bgImg={bgImg} bgFixed={bgFixed}>
          <div className="bgImg_shadow"></div>
          <div className="bgImg "></div>
        </BackgroundImgStyle> */}
      </div>
    );
  }
}
