import React, { Component } from "react";

import HomeButton from "./HomeButton";
import MenuButton from "./MenuButton";

import { NavbarStyle } from "../../2_styles/3_module/NavbarStyle";

// ----------------------

import { withRouter } from "react-router-dom";

export default class Navbar extends Component {
  constructor() {
    super();
    this.state = { pageName: "" };
  }

  getPageName = () => {
    window.location.href === "http://localhost:3000/the-original-six" &&
      this.setState({ pageName: "the original six" });
  };

  render() {
    return (
      <div>
        <NavbarStyle>
          <div id="navbar">
            <HomeButton />
            <div id="pageName">{pageName}</div>
            <MenuButton />
          </div>
        </NavbarStyle>
      </div>
    );
  }
}
