import styled from "styled-components";

export const BackgroundImgStyle = styled.div`
  /* .bgImg_shadow {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImgShadow};

    background: linear-gradient(to top, #111111, transparent 60%);
  }

  .bgImg {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImg};

    background-image: url(${props => props.bgImg});
    background-position: center;
    background-size: cover;
  }

  .bgImg_blur {
    width: 100%;
    height: ${props => (props.height ? props.height : "500px")};

    position: absolute;
    left: 0;
    z-index: ${props => props.zIndexImg};

    background-image: url(${props => props.bgImgBlur});
    background-position: center;
    background-size: cover;

    opacity: 0;
  } */

  /* margin-bottom: 5em; */
  height: 100%;

  .parallax {
    position: absolute;
    height: 100vh;
    width: 100%;
    z-index: -1;
  }

  .parallax > div {
    position: absolute;
  }

  .parallax_bg {
    top: 20%;
    left: ${props => (props.left ? props => props.left : "50%")} !important;
  }

  .bgImg_shadow {
    width: 100%;
    height: 100vh;

    background: linear-gradient(to bottom, transparent, #111111);
  }

  @media only screen and (min-width: 600px) {
    .parallax_bg {
      left: 50% !important ;
    }
  }
`;
