import { BackgroundImgStyle } from "../../2_styles/1_layout/BackgroundImgStyle";

import React, { Component } from "react";
import jquery from "../../jquery";

export default class BackgroundImg extends Component {
  componentDidMount = () => {
    $(window).scroll(function(e) {
      var distanceScrolled = $(this).scrollTop();
      $(".bgImg_blur").css(
        "-webkit-filter",
        "blur(" + distanceScrolled / 60 + "px)"
      );
    });
  };

  render() {
    const {
      bgImg,
      bgImgBlur,
      top,
      height,
      zIndexImg,
      zIndexImgShadow
    } = this.props;
    return (
      <div>
        <BackgroundImgStyle
          bgImg={bgImg}
          bgImgBlur={bgImgBlur}
          top={top}
          height={height}
          zIndexImg={zIndexImg}
          zIndexImgShadow={zIndexImgShadow}
        >
          <div className="bgImg_shadow"></div>
          <div className="bgImg"></div>
          <div className="bgImg_blur"></div>
        </BackgroundImgStyle>
      </div>
    );
  }
}
