import styled from "styled-components";

export const SectionStyle = styled.div`
  margin-bottom: 5em;

  .text {
    /* position: absolute; */
    /* height: 100vh; */

    padding: 0 5vw 0 5vw;
  }

  .title {
    text-align: center;
    text-transform: uppercase;

    font-family: "Cormorant Infant", serif;
    font-size: 2em;

    letter-spacing: 1px;

    margin-bottom: 20px;
    padding-top: 50vh;
  }

  .description {
    text-align: justify;
    text-justify: inter-word;
  }

  p {
    margin-bottom: 1em;
    text-indent: 10px;
  }
`;
