import React from "react";

//COMPONENTS
import Line from "../Line";
import BackgroundImg from "./BackgroundImg";

//STYLES
import { HeaderStyle } from "../../2_styles/1_layout/HeaderStyle";

export default function Header({ bgImg, title, subtitle, height }) {
  return (
    <div>
      <HeaderStyle>
        <BackgroundImg
          bgImg={bgImg}
          top="0"
          height="100vh"
          zIndexImg="-4"
          zIndexImgShadow="-3"
        />
        <div className="title">
          <p>the</p>
          <p>{title}</p>
          <Line />
          <p>{subtitle}</p>
        </div>
      </HeaderStyle>
    </div>
  );
}
