import styled from "styled-components";

export const MenuButtonStyle = styled.button`
  #menu_btn {
    width: 30px;
    height: 25px;

    position: absolute;
    top: 0;
    right: 0;

    margin: 6vw 5vw 0 0;

    cursor: pointer;

    &:hover {
      span {
        :nth-child(1) {
          /* top: -4px; */
          /* width: 110%; */
        }
        :nth-child(3) {
          /* bottom: -4px; */
        }
      }
    }

    span {
      width: 100%;
      height: 2px;

      transition: 0.3s cubic-bezier(0.8, 0.5, 0.2, 1.4);

      position: absolute;

      border-radius: 2px;
      background-color: #f0f0f0;

      &:nth-child(1) {
        top: 0px;
        left: 0px;
      }
      &:nth-child(2) {
        top: 11px;
        left: 0px;
        opacity: 1;
      }
      &:nth-child(3) {
        bottom: 0px;
        left: 0px;
      }
    }
  }
`;

